import React, { useRef } from "react";
import QRCode from "qrcode.react";

const QRform = ({ url, seturl }) => {
  const URLRef = useRef(null);
  const handleGenQR = (e) => {
    e?.preventDefault();
    const urlval = URLRef.current.value.toString();
    console.log(urlval);
    const qr = document.getElementById("#qrcode");
    qr.innerHTML += (
      <QRCode id="qr_code_url" value={urlval} size={200} fgColor="#000000" />
    );
  };

  return (
    <div className="w-full m-3 flex justify-center align-center">
      <form onSubmit={handleGenQR}>
        <input
          type="text"
          name=""
          id=""
          placeholder="Type in a urlðŸ˜"
          className="py-2 w-96 p-2 border-collapse border border-blue-300 rounded-xl text-black m-4"
          ref={URLRef}
        />
        <button
          type="submit"
          className="m-4 px-4 py-2 font-bold bg-blue-500 rounded-xl text-white"
        >
          Submit
        </button>
      </form>
      <div id="qrcode"></div>
    </div>
  );
};

export default QRform;
